plugins/android-native-config.js.content:
const {withAndroidStyles} = require('@expo/config-plugins');

const withAndroidNativeConfig = config => {
  return withAndroidStyles(config, config => {
    // Safely access the styles
    const styles = config.modResults;

    // Ensure we have the basic structure
    if (!styles || !styles.resources) {
      return config;
    }

    // Ensure styles.resources.style exists and is an array
    if (!styles.resources.style || !Array.isArray(styles.resources.style)) {
      styles.resources.style = [];
    }

    // Helper function to safely add text color to a style
    const addTextColorToStyle = styleName => {
      // Find the style element
      const styleElement = styles.resources.style.find(
        style => style && style.$ && style.$.name === styleName,
      );

      if (!styleElement) {
        return; // Style not found, skip
      }

      // Ensure item array exists
      if (!styleElement.item || !Array.isArray(styleElement.item)) {
        styleElement.item = [];
      }

      // Check if text color already exists
      const existingTextColor = styleElement.item.find(
        item => item && item.$ && item.$.name === 'android:textColor',
      );

      if (existingTextColor) {
        // Update existing
        existingTextColor._ = '@android:color/white';
      } else {
        // Add new text color item
        styleElement.item.push({
          $: {name: 'android:textColor'},
          _: '@android:color/white',
        });
      }
    };

    // Apply text color to styles
    addTextColorToStyle('AppTheme');
    addTextColorToStyle('ResetEditText');

    return config;
  });
};

module.exports = withAndroidNativeConfig;

plugins/with-android-notification-icons..content:
const fs = require('fs');
const path = require('path');
const {withDangerousMod} = require('@expo/config-plugins');

/**
 * Ensures ic_notification resources exist after prebuild by copying from assets/android/notification/** into
 * android/app/src/main/res/**. Ships a vector fallback if present.
 */
module.exports = function withAndroidNotificationIcons(config) {
  return withDangerousMod(config, [
    'android',
    async cfg => {
      const projectRoot = cfg.modRequest.projectRoot;
      const srcRoot = path.join(
        projectRoot,
        'assets',
        'android',
        'notification',
      );
      const destRes = path.join(
        projectRoot,
        'android',
        'app',
        'src',
        'main',
        'res',
      );

      if (!fs.existsSync(srcRoot)) {
        // Nothing to copy; keep config intact
        return cfg;
      }

      const copyDir = subdir => {
        const fromDir = path.join(srcRoot, subdir);
        const toDir = path.join(destRes, subdir);
        if (!fs.existsSync(fromDir)) return;
        fs.mkdirSync(toDir, {recursive: true});
        for (const f of fs.readdirSync(fromDir)) {
          const from = path.join(fromDir, f);
          const to = path.join(toDir, f);
          if (fs.statSync(from).isFile()) {
            fs.copyFileSync(from, to);
            // If the asset was misspelled as ic_notificarion.*, also copy a corrected duplicate ic_notification.*
            if (/ic_notificarion(\.[a-z0-9]+)$/i.test(f)) {
              const ext = path.extname(f);
              const corrected = path.join(toDir, `ic_notification${ext}`);
              if (!fs.existsSync(corrected)) {
                fs.copyFileSync(from, corrected);
              }
            }
          }
        }
      };

      // Common resource buckets to copy if present
      const buckets = [
        'drawable-anydpi-v24',
        'drawable',
        'drawable-mdpi',
        'drawable-hdpi',
        'drawable-xhdpi',
        'drawable-xxhdpi',
        'drawable-xxxhdpi',
      ];
      buckets.forEach(copyDir);

      return cfg;
    },
  ]);
};

plugins/with-android-okhttp.js.content:
const fs = require('fs');
const path = require('path');
const {withDangerousMod} = require('@expo/config-plugins');

/**
 * Adds OkHttp dependencies and force resolution strategy after prebuild.
 * Generates a Gradle file that applies OkHttp dependencies and resolution strategy.
 */
module.exports = function withAndroidOkHttp(config) {
  return withDangerousMod(config, [
    'android',
    async cfg => {
      const projectRoot = cfg.modRequest.projectRoot;
      const appDir = path.join(projectRoot, 'android', 'app');
      const buildGradle = path.join(appDir, 'build.gradle');
      const okHttpGradle = path.join(appDir, 'with-okhttp.gradle');

      // Create OkHttp gradle that adds dependencies and force resolution
      const okHttpContent = `// Auto-applied by with-android-okhttp config plugin
dependencies {
    implementation "com.squareup.okhttp3:okhttp:4.12.0"
    implementation "com.squareup.okhttp3:logging-interceptor:4.12.0"
    implementation "com.squareup.okhttp3:okhttp-urlconnection:4.12.0"
}

// Force resolution in case React Native or other libs pull old versions
configurations.all {
    resolutionStrategy {
        force "com.squareup.okhttp3:okhttp:4.12.0"
        force "com.squareup.okhttp3:logging-interceptor:4.12.0"
        force "com.squareup.okhttp3:okhttp-urlconnection:4.12.0"
    }
}
`;
      fs.writeFileSync(okHttpGradle, okHttpContent, 'utf8');

      // Idempotently add apply from with-okhttp.gradle after existing apply lines
      let gradleText = fs.readFileSync(buildGradle, 'utf8');
      if (!gradleText.includes("apply from: 'with-okhttp.gradle'")) {
        // Find the last apply from line and add our line after it
        const applyFromLines = gradleText.match(/apply from: '[^']+'/g);
        if (applyFromLines && applyFromLines.length > 0) {
          const lastApplyFrom = applyFromLines[applyFromLines.length - 1];
          gradleText = gradleText.replace(
            lastApplyFrom,
            `${lastApplyFrom}\napply from: 'with-okhttp.gradle'`,
          );
        } else {
          // If no apply from lines found, add after React plugin
          gradleText = gradleText.replace(
            /apply plugin: "com\.facebook\.react"/,
            `apply plugin: "com.facebook.react"\napply from: 'with-okhttp.gradle'`,
          );
        }
        fs.writeFileSync(buildGradle, gradleText, 'utf8');
      }

      return cfg;
    },
  ]);
};

plugins/with-android-release-gradle.js.content:
const fs = require('fs');
const path = require('path');
const {withDangerousMod} = require('@expo/config-plugins');

/**
 * Ensures custom release build logic (APK renaming and buildTypes settings) is re-applied after prebuild.
 * Adds or updates a Gradle script at android/app/with-release-config.gradle and applies it.
 */
module.exports = function withAndroidReleaseGradle(config) {
  return withDangerousMod(config, [
    'android',
    async cfg => {
      const projectRoot = cfg.modRequest.projectRoot;
      const appDir = path.join(projectRoot, 'android', 'app');
      const buildGradle = path.join(appDir, 'build.gradle');
      const helperGradle = path.join(appDir, 'with-release-config.gradle');
      const abiSplitsGradle = path.join(appDir, 'with-abi-splits.gradle');

      // Write helper gradle to add the APK rename logic if not present
      const helperContent = `// Auto-applied by with-android-release-gradle config plugin
if (project.android) {
  project.android.applicationVariants.all { variant ->
    variant.outputs.each { output ->
      project.ext { appName = 'Vega' }
      def version = variant.versionName
      def newName = output.outputFile.name
            // Keep project.ext.appName as a Gradle variable (escaped from Node template evaluation)
      newName = newName.replace("app-", "${'${'}project.ext.appName${'}'}-")
      newName = newName.replace("-release", "-v" + version)
      output.outputFileName = newName
    }
  }
}
`;
      fs.writeFileSync(helperGradle, helperContent, 'utf8');

      // Write ABI splits gradle which enforces the desired splits
      const abiSplitsContent = `// Auto-applied by with-android-release-gradle config plugin
if (project.android) {
  project.android {
    splits {
      abi {
        enable true
        reset()
        include 'armeabi-v7a', 'arm64-v8a'
        universalApk true
      }
    }
  }
}
`;
      fs.writeFileSync(abiSplitsGradle, abiSplitsContent, 'utf8');

      // Intentionally avoid modifying signing configs here to prevent AGP DSL timing issues.

      // Idempotently ensure the helper is applied in build.gradle
      let gradleText = fs.readFileSync(buildGradle, 'utf8');
      if (!gradleText.includes("apply from: 'with-release-config.gradle'")) {
        // Apply near top of file after existing plugins
        gradleText = gradleText.replace(
          /(apply plugin:[\s\S]*?\n)(?=\n|def|android\s*\{)/,
          `$1apply from: 'with-release-config.gradle'\n`,
        );
      }

      if (!gradleText.includes("apply from: 'with-abi-splits.gradle'")) {
        gradleText = gradleText.replace(
          /(apply plugin:[\s\S]*?\n)(?=\n|def|android\s*\{)/,
          `$1apply from: 'with-abi-splits.gradle'\n`,
        );
      }

      // Remove any stale apply of with-signing-config.gradle (from earlier versions)
      gradleText = gradleText.replace(
        /^apply from: 'with-signing-config.gradle'\s*\n/m,
        '',
      );

      fs.writeFileSync(buildGradle, gradleText, 'utf8');

      return cfg;
    },
  ]);
};

plugins/with-android-signing.js.content:
const fs = require('fs');
const path = require('path');
const {withDangerousMod} = require('@expo/config-plugins');

/**
 * Adds release signing configuration that reads from env vars after prebuild.
 * Uses a Gradle file that applies signing config during the android block evaluation.
 */
module.exports = function withAndroidSigning(config) {
  return withDangerousMod(config, [
    'android',
    async cfg => {
      const projectRoot = cfg.modRequest.projectRoot;
      const appDir = path.join(projectRoot, 'android', 'app');
      const buildGradle = path.join(appDir, 'build.gradle');
      const signingGradle = path.join(appDir, 'with-signing.gradle');

      // Create signing gradle that extends signingConfigs during android block
      const signingContent = `// Auto-applied by with-android-signing config plugin
android {
    signingConfigs {
        release {
            def envStoreFile = System.getenv('MYAPP_UPLOAD_STORE_FILE')
            def envStorePassword = System.getenv('MYAPP_UPLOAD_STORE_PASSWORD')
            def envKeyAlias = System.getenv('MYAPP_UPLOAD_KEY_ALIAS')
            def envKeyPassword = System.getenv('MYAPP_UPLOAD_KEY_PASSWORD')
            
            if (envStoreFile && envStorePassword && envKeyAlias && envKeyPassword) {
                def keystoreFile = file(envStoreFile)
                println "Keystore file path: \${envStoreFile}"
                println "Keystore file exists: \${keystoreFile.exists()}"
                
                if (keystoreFile.exists()) {
                    storeFile keystoreFile
                    storePassword envStorePassword
                    keyAlias envKeyAlias
                    keyPassword envKeyPassword
                    println "Release signing config configured successfully"
                } else {
                    println "Keystore file not found: \${envStoreFile}"
                }
            } else {
                println "Missing signing environment variables:"
                println "  MYAPP_UPLOAD_STORE_FILE: \${envStoreFile}"
                println "  MYAPP_UPLOAD_STORE_PASSWORD: \${envStorePassword ? '***' : 'null'}"
                println "  MYAPP_UPLOAD_KEY_ALIAS: \${envKeyAlias}"
                println "  MYAPP_UPLOAD_KEY_PASSWORD: \${envKeyPassword ? '***' : 'null'}"
            }
        }
    }
}

// Use afterEvaluate to forcefully override the release signing config
afterEvaluate {
    def releaseSigningConfig = android.signingConfigs.release
    println "ðŸ”§ Final signing config check:"
    println "  Release signingConfig storeFile: \${releaseSigningConfig.storeFile}"
    println "  Current release buildType signingConfig: \${android.buildTypes.release.signingConfig?.name}"
    
    if (releaseSigningConfig.storeFile && releaseSigningConfig.storeFile.exists()) {
        // Force override the signing config
        android.buildTypes.release.signingConfig = releaseSigningConfig
        println "âœ… Applied release signing config: \${releaseSigningConfig.storeFile.absolutePath}"
        println "  Final release buildType signingConfig: \${android.buildTypes.release.signingConfig?.name}"
    } else {
        println "âŒ Release signing config not applied, using debug keystore"
        if (releaseSigningConfig.storeFile) {
            println "   Keystore file does not exist: \${releaseSigningConfig.storeFile.absolutePath}"
        } else {
            println "   No keystore file configured"
        }
    }
}
`;
      fs.writeFileSync(signingGradle, signingContent, 'utf8');

      // Idempotently add apply from with-signing.gradle
      let gradleText = fs.readFileSync(buildGradle, 'utf8');
      if (!gradleText.includes("apply from: 'with-signing.gradle'")) {
        // Find the last apply from line and add our line after it
        const applyFromLines = gradleText.match(/apply from: '[^']+'/g);
        if (applyFromLines && applyFromLines.length > 0) {
          const lastApplyFrom = applyFromLines[applyFromLines.length - 1];
          gradleText = gradleText.replace(
            lastApplyFrom,
            `${lastApplyFrom}\napply from: 'with-signing.gradle'`,
          );
        } else {
          // If no apply from lines found, add after React plugin
          gradleText = gradleText.replace(
            /apply plugin: "com\.facebook\.react"/,
            `apply plugin: "com.facebook.react"\napply from: 'with-signing.gradle'`,
          );
        }
        fs.writeFileSync(buildGradle, gradleText, 'utf8');
      }

      return cfg;
    },
  ]);
};

