import {ifExists} from './file/ifExists';
// import {hlsDownloader} from './hlsDownloader';
import * as RNFS from '@dr.pogodin/react-native-fs';
import {Alert} from 'react-native';
import {downloadFolder} from './constants';
import requestStoragePermission from './file/getStoragePermission';
import {hlsDownloader2} from './hlsDownloader2';
import {notificationService} from './services/Notification';

export const downloadManager = async ({
  title,
  url,
  fileName,
  fileType,
  setDownloadActive,
  setAlreadyDownloaded,
  setDownloadId,
  headers,
  deleteDownload,
}: {
  title: string;
  url: string;
  fileName: string;
  fileType: string;
  setDownloadActive: (value: boolean) => void;
  headers?: any;
  setAlreadyDownloaded: (value: boolean) => void;
  setDownloadId: (value: number) => void;
  deleteDownload: () => void;
}) => {
  await requestStoragePermission();

  await notificationService.showDownloadStarting(title, fileName);
  if (await ifExists(fileName)) {
    console.log('File already exists');
    setAlreadyDownloaded(true);
    setDownloadActive(false);
    return;
  }
  setDownloadActive(true);
  // if (activeDownloads.length > 0) {
  //   notifee.displayNotification({
  //     id: 'downloadQueue',
  //     title: 'Download Queue' + fileName,
  //     body: 'Downloading ' + fileName,
  //     android: {
  //       channelId,
  //       color: primary,
  //     },
  //   });
  //   console.log(
  //     'Downloading:',
  //     activeDownloads[0],
  //     activeDownloads[1],
  //     activeDownloads.length,
  //   );

  //   return;
  // }
  try {
    // downloadFile and save it to download folder
    if (!(await RNFS.exists(downloadFolder))) {
      await RNFS.mkdir(downloadFolder);
    }
    await notificationService.requestPermission();

    if (fileType === 'm3u8') {
      // hlsDownloader({
      //   videoUrl: url,
      //   downloadStore,
      //   path: `${downloadFolder}/${fileName}.mp4`,
      //   fileName,
      //   title,
      //   setAlreadyDownloaded,
      //   setDownloadId: setDownloadId,
      //   headers,
      // });
      hlsDownloader2({
        videoUrl: url,
        setDownloadActive,
        path: `${downloadFolder}/${fileName}.mp4`,
        fileName,
        title,
        setAlreadyDownloaded,
        setDownloadId: setDownloadId,
        headers,
      });
      // ToastAndroid.show(
      //   'Hls video download is not supported, Use external Downloader',
      //   ToastAndroid.LONG,
      // );
      // notifee.cancelNotification(fileName);
      // downloadStore.removeActiveDownload(fileName);
      // setAlreadyDownloaded(false);
      console.log('Downloading HLS');
      return;
    }
    const downloadDest = `${downloadFolder}/${fileName}.${fileType}`;
    const ret = RNFS.downloadFile({
      fromUrl: url,
      progressInterval: 1000,
      backgroundTimeout: 1000 * 60 * 60,
      progressDivider: 1,
      headers: headers ? headers : {},
      toFile: downloadDest,
      background: true,
      begin: (res: any) => {
        console.log('Download has started', res);
        setDownloadId(ret.jobId);
      },
      progress: (res: any) => {
        const progress = res.bytesWritten / res.contentLength;
        const body =
          res.contentLength < 1024 * 1024 * 1024
            ? // less than 1GB?

              Math.round(res.bytesWritten / 1024 / 1024) +
              ' / ' +
              Math.round(res.contentLength / 1024 / 1024) +
              ' MB'
            : parseFloat((res.bytesWritten / 1024 / 1024 / 1024).toFixed(2)) +
              ' / ' +
              parseFloat((res.contentLength / 1024 / 1024 / 1024).toFixed(2)) +
              ' GB';
        // console.log('Download progress:', progress * 100);
        notificationService.showDownloadProgress(
          title,
          fileName,
          progress,
          body,
          ret.jobId,
        );
      },
    });
    ret.promise.then(res => {
      console.log('Download complete', res);
      setAlreadyDownloaded(true);
      notificationService.showDownloadComplete(title, fileName);
      setDownloadActive(false);
      // downloadManager({
      //   ...activeDownloads[0],
      //   downloadStore,
      //   setAlreadyDownloaded,
      // });
    });
    ret.promise.catch(err => {
      deleteDownload();
      console.log('Download error:', err);
      Alert.alert('Download failed', err.message || 'Failed to download');
      notificationService.showDownloadFailed(title, fileName);
      setDownloadActive(false);
      setAlreadyDownloaded(false);
      // downloadManager({
      //   ...activeDownloads[0],
      //   downloadStore,
      //   setAlreadyDownloaded,
      // });
    });
    return ret.jobId;
  } catch (error: any) {
    console.error('Download error:', error);
    deleteDownload();
    Alert.alert('Download failed', 'Failed to download');
    setDownloadActive(false);
    setAlreadyDownloaded(false);
  }
};

src/lib/getHomepagedata.ts.content:
import {Content} from './zustand/contentStore';
import {Post} from './providers/types';
import {providerManager} from './services/ProviderManager';

export interface HomePageData {
  title: string;
  Posts: Post[];
  filter: string;
  error?: string;
}

// Optimized version with better error handling
export const getHomePageDataOptimized = async (
  activeProvider: Content['provider'],
  signal: AbortSignal,
): Promise<HomePageData[]> => {
  console.log('Fetching data for provider:', activeProvider.display_name);

  const catalogs = providerManager.getCatalog({
    providerValue: activeProvider.value,
  });

  // Use Promise.allSettled for partial success
  const fetchPromises = catalogs.map(async item => {
    try {
      const data = await providerManager.getPosts({
        filter: item.filter,
        page: 1,
        providerValue: activeProvider.value,
        signal,
      });

      if (signal.aborted) {
        throw new Error('Request aborted');
      }

      console.log(`âœ… Fetched ${data?.length || 0} posts for: ${item.title}`);

      return {
        title: item.title,
        Posts: data || [],
        filter: item.filter,
      };
    } catch (error) {
      console.error(`âŒ Failed to fetch ${item.title}:`, error);

      // Return partial data with error info instead of failing completely
      return {
        title: item.title,
        Posts: [],
        filter: item.filter,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  });

  const results = await Promise.allSettled(fetchPromises);

  // Extract successful results and log failures
  const homePageData: HomePageData[] = [];
  let successCount = 0;
  let failureCount = 0;

  results.forEach((result, index) => {
    if (result.status === 'fulfilled') {
      homePageData.push(result.value);
      if (result.value.Posts.length > 0) {
        successCount++;
      }
    } else {
      failureCount++;
      console.error(
        `Failed to process catalog ${catalogs[index].title}:`,
        result.reason,
      );

      // Add empty category to maintain layout
      homePageData.push({
        title: catalogs[index].title,
        Posts: [],
        filter: catalogs[index].filter,
        error: result.reason?.message || 'Failed to load',
      });
    }
  });

  console.log(
    `ðŸ“Š Results: ${successCount} successful, ${failureCount} failed categories`,
  );

  // Ensure we have at least some data
  if (successCount === 0) {
    throw new Error('Failed to load any content categories');
  }

  return homePageData;
};

// Keep original for backward compatibility
export const getHomePageData = getHomePageDataOptimized;

src/lib/hlsDownloader.ts.content:
import {FFmpegKit, FFprobeKit, ReturnCode} from 'ffmpeg-kit-react-native';
import notifee from '@notifee/react-native';
import {Downloads} from './zustand/downloadsStore';
import {settingsStorage} from './storage';

const getVideoDuration = async (videoUrl: string) => {
  try {
    const information = await FFprobeKit.getMediaInformation(videoUrl);
    const output = await information.getOutput();
    const jsonOutput = JSON.parse(output);
    console.log('Output: ðŸ”¥ðŸ”¥ðŸ”¥', jsonOutput.format.duration);
    const duration = parseFloat(jsonOutput.format.duration);
    return duration;
  } catch (error) {
    console.log('Error getting video duration', error);
    return 0;
  }
};

export const hlsDownloader = async ({
  videoUrl,
  path,
  fileName,
  title,
  downloadStore,
  setAlreadyDownloaded,
  setDownloadId,
  headers = {},
}: {
  videoUrl: string;
  path: string;
  fileName: string;
  title: string;
  downloadStore: Downloads;
  setAlreadyDownloaded: (value: boolean) => void;
  setDownloadId: (value: number) => void;
  headers?: any;
}) => {
  const ffprobeHttpHeaders = headers
    ? Object.entries(headers)
        .map(([key, value]) => `${key}: ${value}`)
        .join('\r\n') + '\r\n'
    : '';
  const command = `-headers "${ffprobeHttpHeaders}" -i ${videoUrl} -c copy -bsf:a aac_adtstoasc -f mp4 -reconnect 1 -reconnect_streamed 1 -reconnect_delay_max 5 -timeout 5000000 -preset ultrafast ${path}`;
  const channelId = await notifee.createChannel({
    id: 'download',
    name: 'Download Notifications',
  });
  const primary = settingsStorage.getPrimaryColor();
  try {
    const duration = (await getVideoDuration(videoUrl)) || 0;
    await FFmpegKit.executeAsync(
      command,
      async session => {
        console.log(
          'FFmpeg process started with sessionId: ' + session.getSessionId(),
        );

        const returnCode = await session.getReturnCode();

        if (ReturnCode.isSuccess(returnCode)) {
          // If download was successful, move the downloaded file into the devices library
          console.log('Download successful');
          setAlreadyDownloaded(true);
          downloadStore.removeActiveDownload(fileName);
          await notifee.cancelNotification(fileName);
          await notifee.displayNotification({
            title: 'Download completed',
            body: `Downloaded ${title}`,
            android: {
              pressAction: {
                id: 'default',
              },
              color: primary,
              smallIcon: 'ic_notification',
              channelId,
            },
          });
        } else {
          setAlreadyDownloaded(false);
          downloadStore.removeActiveDownload(fileName);
          console.log('Download failed');
          await notifee.cancelNotification(fileName);
          await notifee.displayNotification({
            title: 'Download failed',
            body: `Failed to download ${title}`,
            android: {
              pressAction: {
                id: 'default',
              },
              color: primary,
              smallIcon: 'ic_notification',
              channelId,
            },
          });
        }
      },
      async log => {
        const message = log.getMessage();
        const regex = /time=(\d{2}:\d{2}:\d{2}.\d{2})/;
        const currentTime = regex.exec(message as string);
        if (currentTime && currentTime[1]) {
          const currentTimeInSeconds =
            parseInt(currentTime[1].split(':')[0]) * 3600 +
            parseInt(currentTime[1].split(':')[1]) * 60 +
            parseFloat(currentTime[1].split(':')[2]);
          const progress = (currentTimeInSeconds / duration) * 100;
          console.log('Progress: ', currentTimeInSeconds, duration, progress);
          setDownloadId(log.getSessionId());
          await notifee.displayNotification({
            title: title,
            body:
              progress > 100
                ? 'Downloading'
                : `Downloaded ${progress.toFixed(2)}%`,
            id: fileName,
            data: {fileName, jobId: log.getSessionId()},
            android: {
              pressAction: {
                id: 'default',
              },
              smallIcon: 'ic_notification',
              onlyAlertOnce: true,
              progress: {
                max: 100,
                indeterminate: progress > 100,
                current: progress > 100 ? 100 : progress,
              },
              color: primary,
              channelId,
              actions: [
                {
                  title: 'Cancel',
                  pressAction: {
                    id: fileName,
                  },
                },
              ],
            },
          });
        }
      },
    );
  } catch (error) {
    setAlreadyDownloaded(false);
    downloadStore.removeActiveDownload(fileName);
    console.log('Error downloading', error);
    await notifee.displayNotification({
      title: 'Download failed',
      body: `Failed to download ${fileName}`,
      android: {
        pressAction: {
          id: 'default',
        },
        color: primary,
        smallIcon: 'ic_notification',
        channelId,
      },
    });
  }
};

